import React, {useState} from 'react'
import { Link,useHistory} from 'react-router-dom'
import axios from 'axios'
import {showErrMsg, showSuccessMsg} from '../../utils/notification/Notification'
import {isEmpty, isEmail, isLength, isMatch} from '../../utils/validation/Validation'
import {dispatchLogin} from '../../../redux/actions/authAction'
import {useDispatch} from 'react-redux'
import { GoogleLogin } from 'react-google-login';
import FacebookLogin from 'react-facebook-login';


const initialState = {
    name: '',
    email: '',
    mobile:'',
    password: '',
    cf_password: '',
    err: '',
    success: ''
}

function Register() {
    const [user, setUser] = useState(initialState)
    const dispatch = useDispatch()
    const history = useHistory()
    const {name, email, mobile,password,cf_password, err, success} = user

    const handleChangeInput = e => {
        const {name, value} = e.target
        setUser({...user, [name]:value, err: '', success: ''})
    }


    const handleSubmit = async e => {
        e.preventDefault()
        if(isEmpty(name) || isEmpty(password))
                return setUser({...user, err: "Please fill in all fields.", success: ''})

        if(!isEmail(email))
            return setUser({...user, err: "Invalid emails.", success: ''})
        
        // if(!isEmpty(mobile))
        // return setUser({...user, err: "Invalid Mobile No.", success: ''})

        if(isLength(password))
            return setUser({...user, err: "Password must be at least 6 characters.", success: ''})
        
        if(!isMatch(password, cf_password))
            return setUser({...user, err: "Password did not match.", success: ''})

        try {
            const res = await axios.post('/user/register', {
                name, mobile,email, password
            })

            setUser({...user, err: '', success: res.data.msg})
        } catch (err) {
            err.response.data.msg && 
            setUser({...user, err: err.response.data.msg, success: ''})
        }
    }
    const responseGoogle = async (response) => {
        try {
            const res = await axios.post('/user/google_login', {tokenId: response.tokenId})

            setUser({...user, error:'', success: res.data.msg})
            localStorage.setItem('firstLogin', true)

            dispatch(dispatchLogin())
            history.push('/')
        } catch (err) {
            err.response.data.msg && 
            setUser({...user, err: err.response.data.msg, success: ''})
        }
    }

    const responseFacebook = async (response) => {
        try {
            const {accessToken, userID} = response
            const res = await axios.post('/user/facebook_login', {accessToken, userID})

            setUser({...user, error:'', success: res.data.msg})
            localStorage.setItem('firstLogin', true)

            dispatch(dispatchLogin())
            history.push('/')
        } catch (err) {
            err.response.data.msg && 
            setUser({...user, err: err.response.data.msg, success: ''})
        }
    }

    return (
        <div className="login_page">
            <h4 className="arrow_btn"><Link to="/">&#8592;</Link></h4>
            {/* <p className="arrow_btn"><a href="#">	&#8592; </a></p> */}
            <h1 className="heading">{`${"<Signup>"}`}</h1>
            {err && showErrMsg(err)}
            {success && showSuccessMsg(success)}

            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="name" className="input_label">Name</label>
                    <input type="text" placeholder="Enter your name" id="name"
                    value={name} name="name" onChange={handleChangeInput} />
                </div>

                <div>
                    <label htmlFor="email" className="input_label">Email ID</label>
                    <input type="text" placeholder="Enter email address" id="email"
                    value={email} name="email" onChange={handleChangeInput} />
                </div>
                <div>
                    <label htmlFor="mobile" className="input_label">Phone No</label>
                    <input type="number" placeholder="Enter phone number" id="mobile"
                    value={mobile} name="mobile" onChange={handleChangeInput} />
                </div>
                <div>
                    <label htmlFor="password" className="input_label">Password</label>
                    <input type="password" placeholder="Enter password" id="password"
                    value={password} name="password" onChange={handleChangeInput} />
                </div>

                <div>
                    <label htmlFor="cf_password" className="input_label">Confirm Password</label>
                    <input type="password" placeholder="Confirm password" id="cf_password"
                    value={cf_password} name="cf_password" onChange={handleChangeInput} />
                </div>

                <div className="row">
                    <button type="submit" className="btn_login">Register</button>
                </div>
            </form>
            <div>
            <p className="terms"> By clicking here, you have read and agree<br/>
                to <a href="#"><u> Terms of Use</u></a> and <a href="#"><u>Privacy Policy</u></a>{" "}
            </p>
            </div>
            <p className="signup_link">Already a User? <Link to="/login">Login</Link></p>
        
            <p className="socialmedia">
                <span className="footer"> or Continue with</span>
                <img
                className="icon"
                src="https://cdn3.iconfinder.com/data/icons/capsocial-round/500/facebook-512.png"
                />
                <GoogleLogin
                    clientId="Your google client id"
                    render={renderProps => (
                        <button className="google_btn" onClick={renderProps.onClick} disabled={renderProps.disabled}>
                             <img 
                                className="icon" 
                                src="data:image/png;base64,"
                            />
                        </button>
                      )}
                    buttonText=""
                    onSuccess={responseGoogle}
                    cookiePolicy={'single_host_origin'}
                />
                {/* <FacebookLogin
                appId="Your facebook app id"
                autoLoad={false}
                fields="name,email,picture"
                callback={responseFacebook} 
                /> */}

                </p>
        </div>
    )
}

export default Register
